import com.xebialabs.restito.builder.stub.StubHttp.whenHttp
import com.xebialabs.restito.semantics.Action.stringContent
import com.xebialabs.restito.semantics.Condition.alwaysTrue
import com.xebialabs.restito.server.StubServer
import org.junit.jupiter.api.Assertions.assertEquals
import org.junit.jupiter.api.Test


class VkAnalyzeTests {
    val server = StubServer().run()
    val analyzer = VKAnalyze("http://localhost:${server.port}/method/newsfeed.search")

    //private val url = "https://api.vk.com/method/newsfeed.search"/
    @Test
    fun singleQuery() {
        whenHttp(server).match(alwaysTrue())
            .then(stringContent(response))
        val res = analyzer.frequencyByTag("хаски", 1)

        assertEquals( 4, res[0])
    }

    @Test
    fun threeQuery() {
        whenHttp(server).match(alwaysTrue())
            .then(stringContent(response))
        val res = analyzer.frequencyByTag("хаски", 3)

        assertEquals( 4 * 3, res.sum())
    }

